function update_item_amount(frm, item, cdt, cdn) {
    console.log("Calling server method to update amount...");
    frappe.call({
        method: 'hmh_custom_app.custom_api.drug_priscription.on_submit',
        args: {
            patient_encounter: frm.doc.name,
            selected_drug_code: item.drug_code, // Make sure to pass the selected drug code
            dosage: item.dosage,
            period: item.period,
        },
        callback: function(r) {
            console.log("Server response:", r);
            if (r.message) {
                var selected_item_amount = r.message.selected_item_amount;
                var qty = r.message.qty;
                var rate = r.message.rate;

                // Set the amount for the current drug_prescription item
                frappe.model.set_value(cdt, cdn, 'custom_amount', selected_item_amount);
                frappe.model.set_value(cdt, cdn, 'custom_qty', qty);
                frappe.model.set_value(cdt, cdn, 'custom_rate', rate);

                // Optionally, you can also set the rate if needed
                frappe.model.set_value(cdt, cdn, 'rate', item.rate || 0);

        } else {
                frappe.msgprint(__('Error: ') + (r.message ? r.message.message : 'Unknown error'));
            }
        },
        error: function(r) {
            frappe.msgprint(__('Server call failed.'));
            console.error("Server call failed:", r);
        }
    });
}

frappe.ui.form.on('Patient Encounter', {
    encounter_date: function(frm) {
        // Calculate the due date as posting date + 30 days
        var postingDate = new Date(frm.doc.encounter_date);
        postingDate.setDate(postingDate.getDate() + 30);
        
        // Format the date to YYYY-MM-DD
        var dueDate = postingDate.toISOString().split('T')[0];

        // Set the due date in the form
        frm.set_value('custom_due_date', dueDate);
    }
});

